import React from "react";
import axios from "axios";
import DigimonLogo from "./Digimon.png"
import DigiCard from './Components/DigiCard'

import "./styles.css";

class App extends React.Component {
  state={
    digimonsLista:[],
    digiName:"",
    seletor:"Nenhum"
  }

  componentDidMount = () => {
    axios
      .get("https://digimon-api.vercel.app/api/digimon")
      .then(response => {
        this.setState({ digimonsLista: response.data});
      })
      .catch(err => {
        console.log(err);
      });
  };

  changeDigiName = event => {
    this.setState({ digiName: event.target.value, seletor:"digimons"});
  };
render(){
  return (
    <div className="App">

    <select onChange={this.changeDigiName}>
      <option value={""}>{this.state.seletor}</option>
      {this.state.digimonsLista.map((digimon) => {
        return (
          <option key={digimon.name} value={digimon.name}>
            {digimon.name}
          </option>
        );
      })}
    </select>
    {this.state.seletor==="Nenhum"?
    (this.state.digiName && <DigiCard digimon={this.state.digiName}/>): (<img src={DigimonLogo} alt={'Digimon Logo'}/>)}
  </div>
  );
}
}
export default App;
